msg.params = [
    msg.payload.gender,
    msg.payload.age,
    msg.payload.weight,
    msg.payload.height
];
return msg;

// Extract the start_date and end_date from the incoming payload
const startDate = new Date(msg.payload.start_date);
const endDate = new Date(msg.payload.end_date);

// Ensure the dates are formatted as YYYY-MM-DD
msg.params = [
    startDate.toISOString().slice(0, 10), // Get date part only
    endDate.toISOString().slice(0, 10)   // Get date part only
];

return msg;

// The velocity data table
const velocityData = [
    { ageRange: "20 to 29", gender: "Male", velocity: 1.36 },
    { ageRange: "20 to 29", gender: "Female", velocity: 1.34 },
    { ageRange: "30 to 39", gender: "Male", velocity: 1.43 },
    { ageRange: "30 to 39", gender: "Female", velocity: 1.34 },
    { ageRange: "40 to 49", gender: "Male", velocity: 1.43 },
    { ageRange: "40 to 49", gender: "Female", velocity: 1.39 },
    { ageRange: "50 to 59", gender: "Male", velocity: 1.43 },
    { ageRange: "50 to 59", gender: "Female", velocity: 1.31 },
    { ageRange: "60 to 69", gender: "Male", velocity: 1.34 },
    { ageRange: "60 to 69", gender: "Female", velocity: 1.24 },
    { ageRange: "70 to 79", gender: "Male", velocity: 1.26 },
    { ageRange: "70 to 79", gender: "Female", velocity: 1.13 },
    { ageRange: "80 to 89", gender: "Male", velocity: 0.97 },
    { ageRange: "80 to 89", gender: "Female", velocity: 0.94 }
];

// Function to map real age to an age range
function mapAgeToRange(realAge) {
    if (realAge >= 20 && realAge <= 29) return "20 to 29";
    if (realAge >= 30 && realAge <= 39) return "30 to 39";
    if (realAge >= 40 && realAge <= 49) return "40 to 49";
    if (realAge >= 50 && realAge <= 59) return "50 to 59";
    if (realAge >= 60 && realAge <= 69) return "60 to 69";
    if (realAge >= 70 && realAge <= 79) return "70 to 79";
    if (realAge >= 80 && realAge <= 89) return "80 to 89";
    return null; // Out of range
}

// Extract the parameters from the input
const realAge = msg.payload[0].age; // E.g., 32
var gender = msg.payload[0].gender; // E.g., "Male"
if (gender == 0) {
    gender = "Male";
}
else {
    gender = "Female";
}

// Map the real age to an age range
const ageRange = mapAgeToRange(realAge);

if (!ageRange) {
    msg.payload = { error: "Age is out of the supported range (20 to 89)." };
    return msg;
}

// Find the matching velocity from the table
const result = velocityData.find(
    entry => entry.ageRange === ageRange && entry.gender === gender
);

// Return the velocity or an error message if no match is found
if (result) {
    msg.payload = { velocity: result.velocity };
} else {
    msg.payload = { error: "No matching velocity found for the given parameters." };
}
msg.topic = "velocity";
return msg;

node.warn(msg.payload)

// Extract activity, user data, and velocity from msg.payload
const activities = msg.payload.activities; // Array of activities
const userData = msg.payload.userData[0]; // User information
const velocity = msg.payload.velocity.velocity; // Global velocity for all activities

// Validate the input
if (!Array.isArray(activities) || !userData || typeof velocity !== "number") {
    msg.payload = { error: "Invalid input data. Ensure activities, user data, and velocity are provided." };
    return msg;
}

// User data
const weight = userData.weight; // In kilograms
const height = userData.height / 100; // Convert to meters

// Variables to store totals
let totalWalkDistance = 0; // in meters
let totalRunDistance = 0; // in meters
let walkDuration = 0; // in seconds
let runDuration = 0; // in seconds
let totalCaloriesBurnedWalking = 0;
let totalCaloriesBurnedRunning = 0;

// Process activities
activities.forEach((activity, index) => {
    if (index > 0) {
        // Calculate time difference in seconds
        const previousActivity = activities[index - 1];
        const currentTime = new Date(`1970-01-01T${activity.time}Z`);
        const previousTime = new Date(`1970-01-01T${previousActivity.time}Z`);
        const duration = (currentTime.getTime() - previousTime.getTime()) / 1000; // Duration in seconds

        if (duration > 0) {
            if (activity.activity === 0) {
                // Walking
                const walkVelocity = velocity; // Use the global velocity
                totalWalkDistance += walkVelocity * duration; // Distance = velocity * time
                walkDuration += duration; // Add to walking duration

                // Calories calculation using the formula
                const caloriesPerSecond =
                    0.035 * weight + ((walkVelocity ** 2) / height) * 0.029 * weight;
                totalCaloriesBurnedWalking += caloriesPerSecond * duration;
            } else if (activity.activity === 1) {
                // Running
                const runVelocity = velocity * 2; // Running velocity is 2x walking velocity
                totalRunDistance += runVelocity * duration; // Distance = velocity * time
                runDuration += duration; // Add to running duration

                // Calories calculation using the formula
                const caloriesPerSecond =
                    0.035 * weight + ((runVelocity ** 2) / height) * 0.029 * weight;
                totalCaloriesBurnedRunning += caloriesPerSecond * duration;
            }
        }
    }
});

// Convert distances to kilometers
totalWalkDistance /= 1000;
totalRunDistance /= 1000;

// Prepare the result
msg.payload = {
    totalWalkDistance: totalWalkDistance.toFixed(2), // In kilometers
    totalRunDistance: totalRunDistance.toFixed(2), // In kilometers
    walkDuration: (walkDuration / 60).toFixed(2), // In minutes
    runDuration: (runDuration / 60).toFixed(2), // In minutes
    totalCaloriesBurnedWalking: totalCaloriesBurnedWalking.toFixed(2),
    totalCaloriesBurnedRunning: totalCaloriesBurnedRunning.toFixed(2)
};

return msg;

