];

// Function to map real age to an age range
function mapAgeToRange(realAge) {
    if (realAge >= 20 && realAge <= 29) return "20 to 29";
    if (realAge >= 30 && realAge <= 39) return "30 to 39";
    if (realAge >= 40 && realAge <= 49) return "40 to 49";
    if (realAge >= 50 && realAge <= 59) return "50 to 59";
    if (realAge >= 60 && realAge <= 69) return "60 to 69";
    if (realAge >= 70 && realAge <= 79) return "70 to 79";
    if (realAge >= 80 && realAge <= 89) return "80 to 89";
    return null; // Out of range
}

// Extract the parameters from the input
const realAge = user_data.age; // E.g., 32
var gender = user_data.gender; // E.g., "Male"
if (gender == 0) {
    gender = "Male";
}
else {
    gender = "Female";
}

// Map the real age to an age range
const ageRange = mapAgeToRange(realAge);

if (!ageRange) {
    msg.payload = { error: "Age is out of the supported range (20 to 89)." };
    return msg;
}

// Find the matching velocity from the table
const result = velocityData.find(
    entry => entry.ageRange === ageRange && entry.gender === gender
);

// Return the velocity or an error message if no match is found
if (result) {
    msg.payload = { velocity: result.velocity };
} else {
    msg.payload = { error: "No matching velocity found for the given parameters." };
}
msg.topic = "velocity";
return msg;

// Extract activity, user data, and velocity from msg.payload
const activities = msg.payload.activities; // Array of activities
const userData = flow.get("user_data"); // User information
const velocity = msg.payload.velocity.velocity; // Global velocity for all activities

// Validate the input
if (!Array.isArray(activities) || !userData || typeof velocity !== "number") {
    msg.payload = { error: "Invalid input data. Ensure activities, user data, and velocity are provided." };
    return msg;
}

// User data
const weight = userData.weight; // In kilograms
const height = userData.height / 100; // Convert to meters

// Get thresholds from flow context
const thresholds_values = flow.get('thresholds');
const thresholds = {
    daily: {
        walk: thresholds_values.walking,
        run: thresholds_values.running
    },
    weekly: {
        walk: thresholds_values.walking * 7,
        run: thresholds_values.running * 7
    },
    monthly: {
        walk: thresholds_values.walking * 30,
        run: thresholds_values.running * 30
    }

};

if (!thresholds || !thresholds.daily) {
    node.warn("Daily thresholds not set in flow context. Please configure thresholds through the Dashboard.");
    msg.payload = { error: "Daily thresholds not set. Please configure thresholds through the Dashboard." };
    return msg;
}

// Variables to store totals
let totalWalkDistance = 0; // in meters
let totalRunDistance = 0; // in meters
let walkDuration = 0; // in seconds
let runDuration = 0; // in seconds
let totalCaloriesBurnedWalking = 0;
let totalCaloriesBurnedRunning = 0;

// Function to parse time string to seconds
function parseTimeStringToSeconds(timeStr) {
    // timeStr is in format "HH:MM:SS.ssssss"
    const [hoursStr, minutesStr, secondsStr] = timeStr.split(':');
    const hours = parseInt(hoursStr);
    const minutes = parseInt(minutesStr);
    let seconds = 0;
    if (secondsStr.includes('.')) {
        const [wholeSecondsStr, fractionStr] = secondsStr.split('.');
        const wholeSeconds = parseInt(wholeSecondsStr);
        const fraction = parseFloat('0.' + fractionStr);
        seconds = wholeSeconds + fraction;
    } else {
        seconds = parseInt(secondsStr);
    }
    const totalSeconds = hours * 3600 + minutes * 60 + seconds;
    return totalSeconds;
}

// Sort activities by time to ensure correct order
activities.sort((a, b) => parseTimeStringToSeconds(a.time) - parseTimeStringToSeconds(b.time));

// Define maximum allowable duration between activity records (e.g., 5 minutes)
const maxDuration = 5 * 60; // 5 minutes in seconds

// Process activities
for (let i = 0; i < activities.length - 1; i++) {
    const currentActivity = activities[i];
    const nextActivity = activities[i + 1];

    const currentTime = parseTimeStringToSeconds(currentActivity.time);
    const nextTime = parseTimeStringToSeconds(nextActivity.time);
    let duration = nextTime - currentTime; // Duration in seconds

    // Handle time crossing midnight
    if (duration < 0) {
        duration += 24 * 3600; // Add 24 hours in seconds
    }

    // If the gap between activities is too big, do not count this duration
    if (duration > maxDuration) {
        duration = 0;
    }

    if (duration > 0) {
        if (currentActivity.activity === 0) {
            // Walking
            const walkVelocity = velocity; // Use the global velocity
            const distance = walkVelocity * duration;
            totalWalkDistance += distance;
            walkDuration += duration;

            // Calories calculation
            const caloriesPerSecond =
                0.035 * weight + ((walkVelocity ** 2) / height) * 0.029 * weight;
            const caloriesBurned = caloriesPerSecond * duration;
            totalCaloriesBurnedWalking += caloriesBurned;

        } else if (currentActivity.activity === 1) {
            // Running
            const runVelocity = velocity * 2; // Running velocity is 2x walking velocity
            const distance = runVelocity * duration;
            totalRunDistance += distance;
            runDuration += duration;

            // Calories calculation
            const caloriesPerSecond =
                0.035 * weight + ((runVelocity ** 2) / height) * 0.029 * weight;
            const caloriesBurned = caloriesPerSecond * duration;
            totalCaloriesBurnedRunning += caloriesBurned;
        }
    }
}

// Convert durations to minutes
const walkDurationMinutes = walkDuration / 60;
const runDurationMinutes = runDuration / 60;

// Compare to thresholds
const isActiveWalking = walkDurationMinutes >= thresholds.daily.walk;
const isActiveRunning = runDurationMinutes >= thresholds.daily.run;

// Prepare the result
msg.payload = {
    totalWalkDistance: (totalWalkDistance / 1000).toFixed(2), // In kilometers
    totalRunDistance: (totalRunDistance / 1000).toFixed(2), // In kilometers
    walkDuration: walkDurationMinutes.toFixed(2), // In minutes
    runDuration: runDurationMinutes.toFixed(2), // In minutes
    totalCaloriesBurnedWalking: totalCaloriesBurnedWalking.toFixed(2),
    totalCaloriesBurnedRunning: totalCaloriesBurnedRunning.toFixed(2),
    activityStatus: {
        walking: isActiveWalking ? "active" : "sedentary",
        running: isActiveRunning ? "active" : "sedentary"
    }
};

return msg;

